import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import SearchIcon from "@material-ui/icons/Search";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import axios from "axios";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
  button: {
    margin: theme.spacing(2),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

function Search() {
  const classes = useStyles();

  async function getPostData() {
    const jsonData = await axios.get("https://jsonplaceholder.typicode.com/users")
    console.log(jsonData.data)
  }

  return (
    <Button
      variant="contained"
      color="primary"
      className={classes.button}
      endIcon={<SearchIcon />}
      onClick={getPostData()}
    >
      Search
    </Button>
  );
}

function SortSelect() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    sort: "",
  });
  const handleChange = (event) => {
    const name = event.target.name;
    setState({
      ...state,
      [name]: event.target.value,
    });
  };

  return (
    <FormControl variant="outlined" className={classes.formControl}>
      <InputLabel htmlFor="sort-select">Sort</InputLabel>
      <Select
        native
        value={state.sortby}
        onChange={handleChange}
        label="Sort"
        inputProps={{
          name: "sort",
          id: "sort-select",
        }}
      >
        <option aria-label="None" value="" />
        <option value={"n"}>Name, A-Z</option>
        <option value={"c"}>City, A-Z</option>
        <option value={"d"}>Distance</option>
      </Select>
    </FormControl>
  );
}

export default function SearchBars() {
  const classes = useStyles();

  return (
    <React.Fragment>
        <form className={classes.root} noValidate autoComplete="off">
          <TextField id="CitySearch" label="City" variant="outlined" />
          <TextField id="NameSearch" label="Last Name" variant="outlined" />
          <SortSelect />
          <Search />
        </form>
    </React.Fragment>
  );
}
